import React, { useState } from "react";
import "./App.css";

function App() {
  const [folderPath, setFolderPath] = useState("");
  const [message, setMessage] = useState("");

  const handleFolderChange = (event) => {
    // Update the folder path from the input field
    setFolderPath(event.target.value);
    setMessage(""); // Clear any previous messages
  };

  const handleFolderSelect = (event) => {
    // Handle folder selection through the file picker
    const selectedFolder = event.target.files[0];
    if (selectedFolder) {
      const folderName = selectedFolder.webkitRelativePath.split("/")[0];
      setFolderPath(folderName); // Save folder name
      setMessage(`Folder selected: ${folderName}`); // Show success message
    }
  };

  const handleFolderSubmit = () => {
    if (!folderPath) {
      setMessage("Please enter or select a valid folder path.");
      return;
    }
    // Save or use the folder path variable as needed
    console.log("Folder path submitted:", folderPath);
    setMessage(`Folder path saved: ${folderPath}`);
  };

  return (
    <div className="App">
      <h1>Folder Path Selector</h1>

      <p>Choose one of the following methods to provide the folder path:</p>

      {/* Folder Path Input */}
      <div className="folder-input">
        <label htmlFor="folder-path">Enter Folder Path:</label>
        <input
          type="text"
          id="folder-path"
          value={folderPath}
          onChange={handleFolderChange}
          placeholder="Enter the folder path here"
        />
      </div>

      {/* Folder Selection */}
      <div className="folder-input">
        <input
          type="file"
          id="folder-select"
          webkitdirectory="true"
          onChange={handleFolderSelect}
          style={{ display: "none" }}
        />
        <button
          onClick={() => document.getElementById("folder-select").click()}
          className="select-button"
        >
          Select Folder
        </button>
      </div>

      {/* Display selected folder path */}
      {folderPath && (
        <div className="folder-preview">
          <p><strong>Current Folder Path:</strong> {folderPath}</p>
        </div>
      )}

      {/* Submit Button */}
      <button className="submit-button" onClick={handleFolderSubmit}>
        Save Folder Path
      </button>

      {/* Message Display */}
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default App;
























