import React, { useState } from "react";
import "./App.css";

function App() {
  const [folderPath, setFolderPath] = useState("");
  const [message, setMessage] = useState(""); // Success/Error messages
  const [isFolderSelected, setIsFolderSelected] = useState(false); // Track if folder is selected

  const handleFolderChange = (event) => {
    setFolderPath(event.target.value);
    setIsFolderSelected(false); // Reset folder selection state when user types
  };

  const handleFolderSelect = (event) => {
    const selectedFolder = event.target.files[0];
    if (selectedFolder) {
      const folderName = selectedFolder.webkitRelativePath.split("/")[0];
      setFolderPath(folderName); // Update folder path to the folder name
      setIsFolderSelected(true); // Indicate that a folder is selected
    }
  };

  const handleSubmit = () => {
    if (!folderPath) {
      setMessage("Please select or enter a folder path.");
      return;
    }

    setMessage(`Folder path: ${folderPath}`);
    demo(folderPath); // Pass the folder path to the demo function
  };

  // Demo function
  const demo = (path) => {
    console.log("Folder path passed to demo function:", path);
    // Add your logic here to handle the folder path
  };

  return (
    <div className="App">
      <h1>Intellico</h1>

      <p>Choose one of the following methods to provide the folder path:</p>

      <div className="folder-input">
        <label htmlFor="folder-path">Enter Folder Path:</label>
        <input
          type="text"
          id="folder-path"
          value={folderPath}
          onChange={handleFolderChange}
          placeholder="Enter the folder path here"
        />
      </div>

      <div className="folder-input">
        <div
          className={`folder-select-label ${
            isFolderSelected ? "inactive" : ""
          }`}
        >
          <i
            className={`folder-icon ${
              isFolderSelected ? "inactive-icon" : ""
            }`}
          >
            &#128193;
          </i>
          <span
            className={`folder-text ${
              isFolderSelected ? "inactive-text" : ""
            }`}
          >
            Or Select Folder
          </span>
        </div>
        <input
          type="file"
          id="folder-select"
          webkitdirectory="true"
          onChange={handleFolderSelect}
          style={{ display: "none" }}
        />
        <button
          onClick={() => document.getElementById("folder-select").click()}
          className="select-button"
          disabled={isFolderSelected}
        >
          Select Folder
        </button>
      </div>

      {isFolderSelected && folderPath && (
        <div className="folder-preview">
          <p>
            <strong>Selected Folder:</strong> {folderPath}
          </p>
        </div>
      )}

      <button className="submit-button" onClick={handleSubmit}>
        Submit Folder Path
      </button>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default App;























